import React, { useState, useCallback, useRef, useEffect } from "react";
import { useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import {
  ConstructorElement,
  CurrencyIcon,
  Button,
  CloseIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { v4 as uuidv4 } from "uuid";
import styles from "./burgerStyles.module.css";
import { Product } from "../BurgerIngredients/types";
import ModalWindow from "./ModalWindow/ModalWindow";
import CheckMark from "./SVG/CheckMark";
import DraggableIngredient from "./DraggableIngredient";

const generateOrderId = (): string => {
  return Math.floor(1000 + Math.random() * 9000).toString();
};

interface BurgerConstructorProps {
  selectedIngredients: (Product & { uniqueId: string })[];
  onAddIngredient: (ingredient: Product & { uniqueId: string }) => void;
  onRemoveIngredient: (uniqueId: string) => void;
  moveIngredient: (dragIndex: number, hoverIndex: number) => void;
}

const BurgerConstructor: React.FC<BurgerConstructorProps> = ({
  selectedIngredients,
  onAddIngredient,
  onRemoveIngredient,
  moveIngredient,
}) => {
  const noScrollOnRemoveRef = useRef<HTMLDivElement>(null);
  const isMobile = window.innerWidth <= 1023;
  const dropRef = useRef<HTMLUListElement>(null);

  const [{ isHover }, dropTarget] = useDrop({
    accept: "PRODUCT",
    drop: (item: { data: Product }) => {
      const uniqueId = uuidv4();
      const ingredientWithId = { ...item.data, uniqueId };
      if (item.data.type === "bun") {
        // Assuming that you handle bun replacement logic here
        onAddIngredient(ingredientWithId);
      } else {
        onAddIngredient(ingredientWithId);
      }
    },
    collect: (monitor) => ({
      isHover: monitor.isOver(),
    }),
  });

  useEffect(() => {
    const noScrollElem = noScrollOnRemoveRef.current;


    return () => {

    };
  }, []);

  const [isModalActive, setIsModalActive] = useState(false);
  const [orderId, setOrderId] = useState<string | null>(null);

  const totalPrice = selectedIngredients.reduce(
    (acc, ingredient) => acc + ingredient.price,
    0
  );

  const bun = selectedIngredients.find(
    (ingredient) => ingredient.type === "bun"
  );
  const otherIngredients = selectedIngredients.filter(
    (ingredient) => ingredient.type !== "bun"
  );

  const handleModalClose = () => {
    setIsModalActive(false);
  };

  const handleOrderClick = () => {
    const newOrderId = generateOrderId();
    setOrderId(newOrderId);
    setIsModalActive(true);
  };

  const moveIngredientCallback = useCallback(
    (dragIndex: number, hoverIndex: number) => {
      moveIngredient(dragIndex, hoverIndex);
    },
    [moveIngredient]
  );

  const borderColor = isHover ? "#8585AD" : "transparent";

  return (
    <>
      <DndProvider backend={HTML5Backend}>
        <section
          className={styles.contentConstructor}
          ref={noScrollOnRemoveRef}
        >
          <h2 className="text text_type_main-medium"></h2>
          <div
            className={styles.listConstructor}
            ref={dropTarget}
            style={{ borderColor }}
          >
            {bun && (
              <div className={styles.constructorElement}>
                <ConstructorElement
                  type="top"
                  isLocked={true}
                  text={`${bun.name} (верх)`}
                  price={bun.price}
                  thumbnail={bun.image}
                />
              </div>
            )}
            <ul className={styles.inner_style} ref={dropRef}>
              {otherIngredients.map((ingredient, index) => {
                const newItem = { ...ingredient, index };
                const lastIndex = index === otherIngredients.length - 1;
                return (
                  <DraggableIngredient
                    key={newItem.uniqueId}
                    index={index}
                    ingredient={newItem}
                    moveCard={moveIngredientCallback}
                    onRemoveIngredient={onRemoveIngredient}
                  />
                );
              })}
            </ul>
            {bun && (
              <div className={styles.constructorElement}>
                <ConstructorElement
                  type="bottom"
                  isLocked={true}
                  text={`${bun.name} (низ)`}
                  price={bun.price}
                  thumbnail={bun.image}
                />
              </div>
            )}
          </div>
        </section>
        <section className={`ml-1 mr-1 mb-1 mt-9 ${styles.boxForButton}`}>
          <div className={styles.footerOrder}>
            <div>
              <p className="text text_type_digits-medium">{totalPrice}</p>
            </div>
            <div>
              <CurrencyIcon type="primary" />
            </div>
            <Button
              htmlType="button"
              type="primary"
              size="large"
              onClick={handleOrderClick}
            >
              Оформить заказ
            </Button>
          </div>
        </section>

        <ModalWindow active={isModalActive} setActive={setIsModalActive}>
          <div className={styles.boxModalClose}>
            <div className={`p-2`} style={{ cursor: "pointer" }}>
              <CloseIcon type="primary" onClick={handleModalClose} />
            </div>
          </div>
          <p className="mt-8 text text_type_digits-large">{orderId}</p>
          <p className="mb-10 text text_type_main-medium">
            Идентификатор заказа
          </p>
          <div>
            <CheckMark />
          </div>
          <p className="mt-10 text text_type_main-small">
            {" "}
            Ваш заказ начали готовить
          </p>
          <p className=" mt-2 text text_type_main-default text_color_inactive">
            Дождитесь готовности на орбитальной станции
          </p>
        </ModalWindow>
      </DndProvider>
    </>
  );
};

export default BurgerConstructor;



const opts: AddEventListenerOptions & EventListenerOptions = { passive: false };
