import React, {useState} from "react";
import { useDrop } from "react-dnd";
import {
  ConstructorElement,
  DragIcon,
  CurrencyIcon,
  Button,
  CloseIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./burgerStyles.module.css";
import { Product } from "../BurgerIngredients/types"; // Подставьте свой тип продукта здесь
import ModalWindow from "./ModalWindow/ModalWindow"; 
import CheckMark from "./SVG/CheckMark";

interface BurgerConstructorProps {
  selectedIngredients: Product[];
  onAddIngredient: (ingredient: Product) => void;
  onRemoveIngredient: (index: number) => void;
}

const BurgerConstructor: React.FC<BurgerConstructorProps> = ({
  selectedIngredients,
  onAddIngredient,
  onRemoveIngredient,
}) => {
  const [{ isOver }, drop] = useDrop({
    accept: "PRODUCT",
    drop: (item: { data: Product }) => {
      if (
        item.data.type === "bun" &&
        selectedIngredients.some((ingredient) => ingredient.type === "bun")
      ) {
        return;
      }
      onAddIngredient(item.data);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  const [isModalActive, setModalActive] = useState(false);

  const totalPrice = selectedIngredients.reduce(
    (acc, ingredient) => acc + ingredient.price,
    0
  );

  const bun = selectedIngredients.find(
    (ingredient) => ingredient.type === "bun"
  );
  const otherIngredients = selectedIngredients.filter(
    (ingredient) => ingredient.type !== "bun"
  );

  return (
    <>
      <section className={styles.contentConstructor} ref={drop}>
        <h2 className="text text_type_main-medium">Ваш бургер</h2>
        <div className={styles.listConstructor}>
          {bun && (
            <div className={styles.constructorElement}>
              <ConstructorElement
                type="top"
                isLocked={true}
                text={`${bun.name} (верх)`}
                price={bun.price}
                thumbnail={bun.image}
              />
            </div>
          )}
          {otherIngredients.map((ingredient: Product, index: number) => (
            <div
              key={index}
              className={styles.constructorElement}
              onClick={() => onRemoveIngredient(index)}
            >
              <DragIcon type="primary" />
              <ConstructorElement
                text={ingredient.name}
                price={ingredient.price}
                thumbnail={ingredient.image}
                handleClose={() => onRemoveIngredient(index)}
              />
            </div>
          ))}
          {bun && (
            <div className={styles.constructorElement}>
              <ConstructorElement
                type="bottom"
                isLocked={true}
                text={`${bun.name} (низ)`}
                price={bun.price}
                thumbnail={bun.image}
              />
            </div>
          )}
        </div>
      </section>
      <section className={`ml-1 mr-1 mb-1 mt-9 ${styles.boxForButton}`}>
        <div className={styles.footerOrder}>
          <div>
            <p className="text text_type_digits-medium">{totalPrice}</p>
          </div>
          <div>
            <CurrencyIcon type="primary" />
          </div>
          <Button
            htmlType="button"
            type="primary"
            size="large"
            onClick={() => setModalActive(true)}
          >
            Оформить заказ
          </Button>
        </div>
      </section>
      <ModalWindow active={isModalActive} setActive={setModalActive}>
        <h2>Ваш заказ оформлен!</h2>
        <p>Спасибо за ваш заказ. Он будет готов в ближайшее время.</p>
      </ModalWindow>
    </>
  );
};

export default BurgerConstructor;
