import React from "react";
import ingridient from "./ingridient.module.css";
import { Product } from "./types";
import ModalWindow from "../../../ui/ModalWindow/ModalWindow";
import  IngredientDetails  from "./IngredientDetails";

interface ProductDetailsModalProps {
  product: Product | null;
  isActive: boolean;
  onClose: () => void;
}

const ProductDetailsModal: React.FC<ProductDetailsModalProps> = ({
  product,
  isActive,
  onClose,
}) => {
  if (!product) return null;

  return (
    <ModalWindow active={isActive} setActive={onClose}>
      <IngredientDetails
        name={product.name}
        calories={product.calories}
        proteins={product.proteins}
        fat={product.fat}
        carbohydrates={product.carbohydrates}
        image_large={product.image_large}
      />
      <div>
        <div>
          <div className={ingridient.modalTitle}>
            <img
              className={ingridient.modalImage}
              src={product.image_large}
              alt={product.name}
            />
            <h3>{product.name}</h3>
          </div>
          <div className={ingridient.ingridentModal}>
            <ul
              className={`text text_type_main-default ${ingridient.modalContentUl}`}
            >
              <li
                className={`text text_type_main-default ${ingridient.modalContentLi}`}
              >
                Калории, ккал
              </li>
              <li
                className={`text text_type_main-default ${ingridient.modalContentLi}`}
              >
                {product.calories}
              </li>
            </ul>
            <ul className={ingridient.modalContentUl}>
              <li
                className={`text text_type_main-default ${ingridient.modalContentLi}`}
              >
                Белки, г
              </li>
              <li className={ingridient.modalContentLi}>{product.proteins}</li>
            </ul>
            <ul className={ingridient.modalContentUl}>
              <li
                className={`text text_type_main-default ${ingridient.modalContentLi}`}
              >
                Жиры, г
              </li>
              <li
                className={`text text_type_main-default ${ingridient.modalContentLi}`}
              >
                {product.fat}
              </li>
            </ul>
            <ul
              className={`text text_type_main-default ${ingridient.modalContentUl}`}
            >
              <li
                className={`text text_type_main-default ${ingridient.modalContentLi}`}
              >
                Углеводы, г
              </li>
              <li className={ingridient.modalContentLi}>
                {product.carbohydrates}
              </li>
            </ul>
          </div>
        </div>
      </div>
    </ModalWindow>
  );
};

export default ProductDetailsModal;
