import React, { useRef, useState, useEffect } from "react";

import ingridient from "./ingridient.module.css";
import modal from "./modal.module.css";
import {
  CurrencyIcon,
  Tab,
} from "@ya.praktikum/react-developer-burger-ui-components";
import ModalWindow from "../../../ui/ModalWindow/ModalWindow";
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";

// Типизация для бля блока "Продукты"
interface Product {
  _id: string;
  name: string;
  type: string;
  proteins: number;
  fat: number;
  carbohydrates: number;
  calories: number;
  price: number;
  image: string;
  image_large: string;
}

// Функция сортировки с типизацией
const sortProducts = (products: Product[]): Product[] => {
  const order = ["bun", "sauce", "main"];
  return products
    .slice()
    .sort((a, b) => order.indexOf(a.type) - order.indexOf(b.type));
};

const ProductItem: React.FC<{
  data: Product;
   onClick:(product: Product) => void;
}> = ({data, onClick}) => {

  return (
    <li className={ingridient.ingridientsItem} onClick={() => onClick(data)}>
      <img className="ml-2 mr-2" src={data.image} alt={data.name} />
      <div className={ingridient.priceBox}>
        <p className={`text text_type_main-default`}>{data.price}</p>
        <CurrencyIcon type="primary" />
      </div>
      <h3 className={`text text_type_main-default`}>{data.name}</h3>
    </li>
  );
};

const BurgerIngredients: React.FC = () => {

    const [state, setState] = useState<{
      isLoading: boolean;
      hasError: boolean;
      data: Product[];
    }>({
      isLoading: false,
      hasError: false,
      data: [],
    });
  const [current, setCurrent] = React.useState("bun");

  // Рефы для каждого раздела
  const bunRef = useRef<HTMLDivElement>(null);
  const sauceRef = useRef<HTMLDivElement>(null);
  const mainRef = useRef<HTMLDivElement>(null);

  // Состояние для модального окна
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [isModalActive, setIsModalActive] = useState(false);

  // Функция для прокрутки к выбранному разделу
  const handleTabClick = (value: string) => {
    setCurrent(value);
    switch (value) {
      case "bun":
        bunRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "sauce":
        sauceRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "main":
        mainRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      default:
        break;
    }
  };

  // Функция для открытия модального окна
  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    setIsModalActive(true);
  };
  // Функция для закрытия модального окна
  const handleModalClose = () => {
    setIsModalActive(false);
  };

useEffect(() => {
const fetchProducts = () => {
setState({isLoading:true, hasError: false, data: []});
fetch("https://norma.nomoreparties.space/api/ingredients")
  .then((res) => res.json())
  .then((data) =>
    setState({
      isLoading: false,
      hasError: false,
      data: sortProducts(data.data),
    })
  )
  .catch(() => setState({ isLoading: false, hasError: true, data: [] }));
};
fetchProducts();
}, []);
const {data, isLoading, hasError} = state;

  return (
    <>
      <h1
        className={`mt-9 mb-6  text text_type_main-large ${ingridient.ingridientTitle}`}
      >
        Список продуктов
      </h1>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={current === "bun"}
          onClick={() => handleTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={current === "sauce"}
          onClick={() => handleTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={current === "main"}
          onClick={() => handleTabClick("main")}
        >
          Начинки
        </Tab>
      </div>
      {isLoading && <p>Загрузка... </p>}
      {hasError && <p>Произошла ошибка</p>}
      {!isLoading && !hasError && (
        <section className={ingridient.ingridients}>
          <div className="mt-9 mb-9" ref={bunRef}>
            <h2
              className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
            >
              Булки
            </h2>
            <ul className={ingridient.ingridientsContainer}>
              {data
                .filter((product) => product.type === "bun")
                .map((product) => (
                  <ProductItem
                    key={product._id}
                    data={product}
                    onClick={handleProductClick}
                  />
                ))}
            </ul>
          </div>
          <div ref={sauceRef}>
            <h2
              className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
            >
              Соусы
            </h2>
            <ul className={ingridient.ingridientsContainer}>
              {data
                .filter((product) => product.type === "sauce")
                .map((product) => (
                  <ProductItem
                    key={product._id}
                    data={product}
                    onClick={handleProductClick}
                  />
                ))}
            </ul>
          </div>
          <div ref={mainRef} className="">
            <h2
              className={`text text_type_main-medium ${ingridient.ingridientTitle}`}
            >
              Начинки
            </h2>
            <ul className={ingridient.ingridientsContainer}>
              {data
                .filter((product) => product.type === "main")
                .map((product) => (
                  <ProductItem
                    key={product._id}
                    data={product}
                    onClick={handleProductClick}
                  />
                ))}
            </ul>
          </div>
        </section>
      )}
      {selectedProduct && (
        <ModalWindow active={isModalActive} setActive={setIsModalActive}>
          <div className={modal.active}>
            <div className={ingridient.ModalHeader}>
              <h2 className="text text_type_main-large">Детали ингредиента</h2>
              <div className={`p-2`} style={{ cursor: "pointer" }}>
                <CloseIcon type="primary" onClick={() => handleModalClose()} />
              </div>
            </div>
            <div>
              <div>
                <img
                  src={selectedProduct.image_large}
                  alt={selectedProduct.name}
                />
                <h3>{selectedProduct.name}</h3>
              </div>
              <div className={ingridient.ingridentModal}>
                <ul
                  className={`text text_type_main-default ${ingridient.modalContentUl}`}
                >
                  <li
                    className={`text text_type_main-default ${ingridient.modalContentLi}`}
                  >
                    Калории, ккал
                  </li>
                  <li
                    className={`text text_type_main-default ${ingridient.modalContentLi}`}
                  >
                    {selectedProduct.calories}
                  </li>
