import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { RootState } from "../../redux/store";
import { closeModal } from "./modalSlice"; // Подставьте правильный путь к экшену closeModal
import styles from "./ModalWindow.module.css";

interface ModalWindowProps {
  children: React.ReactNode;
}

const ModalWindow: React.FC<ModalWindowProps> = ({ children }) => {
  const dispatch = useDispatch();
  const isModalActive = useSelector(
    (state: RootState) => state.modal.isModalActive
  );

  useEffect(() => {
    const handleModalKeyClose = (event: KeyboardEvent) => {
      if (event.key === "Escape") {
        dispatch(closeModal());
      }
    };

    if (isModalActive) {
      window.addEventListener("keydown", handleModalKeyClose);
    }

    return () => {
      window.removeEventListener("keydown", handleModalKeyClose);
    };
  }, [dispatch, isModalActive]);

  const handleOverlayClick = () => {
    dispatch(closeModal());
  };

  return (
    <div
      className={
        isModalActive ? `${styles.modal} ${styles.active}` : styles.modal
      }
      onClick={handleOverlayClick}
    >
      <div
        className={styles.modal__content}
        onClick={(e) => e.stopPropagation()}
      >
        {children}
      </div>
    </div>
  );
};

export default ModalWindow;
