import React, { useState } from "react";
import { useDrop, DropTargetMonitor } from "react-dnd";
import {
  ConstructorElement,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./burgerStyles.module.css";
import { Product } from "./types"; // Подставьте свой тип продукта здесь

const BurgerConstructor: React.FC = () => {
  const [burgerIngredients, setBurgerIngredients] = useState<Product[]>([]);

  // Функция для обработки события перетаскивания
  const handleDrop = (item: Product) => {
    setBurgerIngredients((prevIngredients) => [...prevIngredients, item]);
  };

  const [{ isOver }, drop] = useDrop({
    accept: "INGREDIENT",
    drop: (item: Product) => handleDrop(item),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  return (
    <section className={styles.contentConstructor}>
      <div
        className={`${styles.listConstructor} ${isOver && styles.isOver}`}
        ref={drop}
      >
        {burgerIngredients.map((ingredient, index) => (
          <div key={ingredient._id}>
            <DragIcon type="primary" />
            <ConstructorElement
              text={ingredient.name}
              price={ingredient.price}
              thumbnail={ingredient.image_mobile}
            />
          </div>
        ))}
        {isOver && <div className={styles.dragOverOverlay}></div>}
      </div>
    </section>
  );
};

export default BurgerConstructor;
