import React from "react";
import products, { Product } from "../../../utils/data";

import {
  Tab,
  Button,
} from "@ya.praktikum/react-developer-burger-ui-components";

const BurgerIngridients: React.FC = () => {

    const [current, setCurrent] = React.useState("bun");
    const tabClick = (value: string) => {
      setCurrent(value);
    };

    const filterProducts = products.filter(
      (product: Product) => product.type === current
    );

  return (
    <>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={current === "bun"}
          onClick={() => tabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={current === "sauce"}
          onClick={() => tabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={current === "main"}
          onClick={() => tabClick("main")}
        >
          Начинки
        </Tab>
      </div>
 <ul style={{ display: "flex", width: "600px", listStyleType: "none", padding: 0 }}>
      {filterProducts.map((data: Product) => (
        <li
          key={data._id}
          style={{
            marginBottom: "20px",
            borderBottom: "1px solid #ccc",
            paddingBottom: "20px",
            width: "272px",
            height: "208",
          }}
        >
          <h2>{data.name}</h2>
          <p>Тип: {data.type}</p>
          <p>Протеин: {data.proteins}</p>
          <p>Жиры: {data.fat}</p>
          <p>Углеводы: {data.carbohydrates}</p>
          <p>Калории: {data.calories}</p>
          <p>Цена: {data.price} руб.</p>
          <img
            src={data.image}
            alt={data.name}
            style={{ width: "150px", height: "auto" }}
          />
        </li>
      ))}
</ul>

    </>
  );
};
export default BurgerIngridients;
