import React, { useState } from "react";
import { useDrop} from "react-dnd";
import {
  ConstructorElement,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./burgerStyles.module.css";
import { Product } from "../BurgerIngredients/types"; // Подставьте свой тип продукта здесь


interface BurgerConstructorProps {
  selectedIngredients: Product[];
  onAddIngredient: (ingredient: Product) => void;
}

const BurgerConstructor: React.FC<BurgerConstructorProps> = ({
  selectedIngredients,
  onAddIngredient,
}) => {
  const [{ isOver }, drop] = useDrop({
    accept: "PRODUCT",
    drop: (item: { data: Product }) => {
      onAddIngredient(item.data);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  return (
    <div
      ref={drop}
      
    >
      <h2 className="text text_type_main-medium">Ваш бургер</h2>
      <ul >
        {selectedIngredients.map((ingredient: Product, index: number) => (
          <li key={index} >
            <img
              src={ingredient.image}
              alt={ingredient.name}
              
            />
            <p className="text text_type_main-default">{ingredient.name}</p>
            <p className="text text_type_digits-default">{ingredient.price}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BurgerConstructor;
