import React, { useState, useEffect } from "react";
import {
  ConstructorElement,
  CurrencyIcon,
  Button,
  CloseIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./burgerStyles.module.css";
import { Product } from "../BurgerIngredients/types"; // Подставьте свой тип продукта здесь
import ModalWindow from "./ModalWindow/ModalWindow";
import CheckMark from "./SVG/CheckMark";
import DraggableIngredient from "./DraggableIngredient";

// Функция генерации идентификатора заказа
const generateOrderId = (): string => {
  return Math.floor(1000 + Math.random() * 9000).toString(); // Генерация четырехзначного числа
};

interface BurgerConstructorProps {
  selectedIngredients: (Product & { uniqueId: string })[];
  onAddIngredient: (ingredient: Product) => void;
  onRemoveIngredient: (uniqueId: string) => void;
  moveIngredient: (dragIndex: number, hoverIndex: number) => void;
}

const BurgerConstructor: React.FC<BurgerConstructorProps> = ({
  selectedIngredients,
  onAddIngredient,
  onRemoveIngredient,
  moveIngredient,
}) => {
  const [elements, setElements] = useState<(Product & { uniqueId: string })[]>(
    []
  );
  const [draggedElement, setDraggedElement] = useState<
    (Product & { uniqueId: string }) | null
  >(null);
  const [isModalActive, setIsModalActive] = useState(false);
  const [orderId, setOrderId] = useState<string | null>(null);

  useEffect(() => {
    setElements(selectedIngredients);
  }, [selectedIngredients]);

  const handleDrag = (
    e: React.DragEvent,
    ingredient: Product & { uniqueId: string }
  ) => {
    e.preventDefault();
    setDraggedElement(ingredient);
  };

  const handleDrop = (e: React.DragEvent, index: number) => {
    e.preventDefault();
    if (draggedElement) {
      const newElements = [...elements];
      const draggedIndex = newElements.findIndex(
        (el) => el.uniqueId === draggedElement.uniqueId
      );
      newElements.splice(draggedIndex, 1);
      newElements.splice(index, 0, draggedElement);
      setElements(newElements);
      moveIngredient(draggedIndex, index);
    }
  };

  const totalPrice = elements.reduce(
    (acc, ingredient) => acc + ingredient.price,
    0
  );

  const bun = elements.find((ingredient) => ingredient.type === "bun");
  const otherIngredients = elements.filter(
    (ingredient) => ingredient.type !== "bun"
  );

  // Функция для закрытия модального окна
  const handleModalClose = () => {
    setIsModalActive(false);
  };

  // Функция для открытия модального окна и генерации идентификатора заказа
  const handleOrderClick = () => {
    const newOrderId = generateOrderId();
    setOrderId(newOrderId);

    setIsModalActive(true);
  };

  return (
    <>
      <section className={styles.contentConstructor}>
        <h2 className="text text_type_main-medium"></h2>
        <div className={styles.listConstructor}>
          {bun && (
            <div className={styles.constructorElement}>
              <ConstructorElement
                type="top"
                isLocked={true}
                text={`${bun.name} (верх)`}
                price={bun.price}
                thumbnail={bun.image}
              />
            </div>
          )}
          {otherIngredients.map((ingredient, index) => (
            <div
              key={ingredient.uniqueId}
              onDragOver={(e) => e.preventDefault()}
              onDrop={(e) => handleDrop(e, index)}
            >
              <DraggableIngredient
                ingredient={ingredient}
                onDragHandler={handleDrag}
                onRemoveIngredient={onRemoveIngredient}
              />
            </div>
          ))}
          {bun && (
            <div className={styles.constructorElement}>
              <ConstructorElement
                type="bottom"
                isLocked={true}
                text={`${bun.name} (низ)`}
                price={bun.price}
                thumbnail={bun.image}
              />
            </div>
          )}
        </div>
      </section>
      <section className={`ml-1 mr-1 mb-1 mt-9 ${styles.boxForButton}`}>
        <div className={styles.footerOrder}>
          <div>
            <p className="text text_type_digits-medium">{totalPrice}</p>
          </div>
          <div>
            <CurrencyIcon type="primary" />
          </div>
          <Button
            htmlType="button"
            type="primary"
            size="large"
            onClick={handleOrderClick}
          >
            Оформить заказ
          </Button>
        </div>
      </section>

      <ModalWindow active={isModalActive} setActive={setIsModalActive}>
        <div className={styles.boxModalClose}>
          <div className={`p-2`} style={{ cursor: "pointer" }}>
            <CloseIcon type="primary" onClick={handleModalClose} />
          </div>
        </div>
        <p className="mt-8 text text_type_digits-large">{orderId}</p>
        <p className="mb-10 text text_type_main-medium">Идентификатор заказа</p>
        <div>
          <CheckMark />
        </div>
        <p className="mt-10 text text_type_main-small">
          {" "}
          Ваш заказ начали готовить
        </p>
        <p className=" mt-2 text text_type_main-default text_color_inactive">
          Дождитесь готовности на орбитальной станции
        </p>
      </ModalWindow>
    </>
  );
};

export default BurgerConstructor;
