import React, { useEffect, useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchIngredients } from "../../../services/actions/ingredientsActions";
import { Product } from "../../../types/Product";
import styles from "./styles.module.css";
import {
  Tab,
  CurrencyIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { useInView } from "react-intersection-observer";
import { useDrag } from "react-dnd";
import ProductDetailsModal from "./ProductDetailsModal";
import { RootState } from "../../../services/store";

interface IngredientsState {
  ingredients: Product[] | null;
  loading: boolean;
  error: string | null;
}

interface IngredientProps {
  data: Product;
  count: number;
  onClick: (data: Product) => void;
}

const Ingredient: React.FC<IngredientProps> = ({ data, count, onClick }) => {
  const [{ isDragging }, dragRef] = useDrag(() => ({
    type: "ingredient",
    item: { data },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  const image = <img src={data.image} alt={data.name} />;

  return (
    <div
      ref={dragRef}
      className={styles.ingridientsItem}
      style={{ opacity: isDragging ? 0.5 : 1 }}
      onClick={() => onClick(data)}
    >
      <li>
        <div className={styles.img}>{image}</div>
        <p className={styles.name}>{data.name}</p>
        <div className={styles.priceBox}>
          <p className={styles.description}>{`${data.price}`}</p>
          <CurrencyIcon type="primary" />
        </div>
      </li>
      <span className={count ? styles.count : ""}>{count}</span>
    </div>
  );
};

interface BurgerIngredientsProps {
  ingredientCount: { [key: string]: number };
}

const BurgerIngredients: React.FC<BurgerIngredientsProps> = ({
  ingredientCount,
}) => {
  const dispatch = useDispatch();

  // Обновляем типизацию для useSelector
  const { ingredients, loading, error } = useSelector(
    (state: RootState) => state.ingredients
  );

  useEffect(() => {
    dispatch(fetchIngredients());
  }, [dispatch]);

  if (loading) {
    return <div>Загрузка...</div>;
  }

  if (error) {
    return <div>Ошибка: {error}</div>;
  }

  if (!ingredients) {
    return <div>Ингредиенты отсутствуют</div>;
  }

  const [current, setCurrent] = useState("bun");

  const bunRef = useRef<HTMLDivElement>(null);
  const sauceRef = useRef<HTMLDivElement>(null);
  const mainRef = useRef<HTMLDivElement>(null);

  const [bunInViewRef, bunInView] = useInView({ threshold: 0.3 });
  const [sauceInViewRef, sauceInView] = useInView({ threshold: 0.2 });
  const [mainInViewRef, mainInView] = useInView({ threshold: 0.5 });

  useEffect(() => {
    if (bunInView) {
      setCurrent("bun");
    } else if (sauceInView) {
      setCurrent("sauce");
    } else if (mainInView) {
      setCurrent("main");
    }
  }, [bunInView, sauceInView, mainInView]);

  const handleTabClick = (value: string) => {
    setCurrent(value);
    switch (value) {
      case "bun":
        bunRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "sauce":
        sauceRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "main":
        mainRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      default:
        break;
    }
  };

  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [isModalActive, setIsModalActive] = useState(false);
  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    setIsModalActive(true);
  };
  const handleModalClose = () => {
    setIsModalActive(false);
  };

  return (
    <section>
      <h1 className={`mt-9 mb-6 text text_type_main-large`}>
        Список продуктов
      </h1>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={current === "bun"}
          onClick={() => handleTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={current === "sauce"}
          onClick={() => handleTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={current === "main"}
          onClick={() => handleTabClick("main")}
        >
          Начинки
        </Tab>
      </div>
      <div className={styles.ingridients}>
        {ingredients.map((ingredient) => (
          <Ingredient
            key={ingredient._id}
            data={ingredient}
            count={ingredientCount[ingredient._id] || 0}
            onClick={handleProductClick}
          />
        ))}
      </div>
      <ProductDetailsModal
        product={selectedProduct}
        isActive={isModalActive}
        onClose={handleModalClose}
      />
    </section>
  );
};

export default BurgerIngredients;
