import React from "react";

interface OrderDetailsProps {
  orderId: string | null;
  totalPrice: number;
  children?: React.ReactNode;
}

const OrderDetails: React.FC<OrderDetailsProps> = ({ orderId, totalPrice, children }) => {
  return (
    <>
      <p className="mt-8 text text_type_digits-large">{orderId}</p>
      <p className="text text_type_digits-medium">Total Price: {totalPrice}</p>
    
      {children}
    </>
  );
};

export default OrderDetails;
