import React, { useRef, useState } from "react";
import products from "../../../utils/data";
import ingredient from "./ingredient.module.css";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import Modal from "../../../utils/modal-test/testModal";

// Типизация для продукта
interface Product {
  _id: string;
  name: string;
  type: string;
  proteins: number;
  fat: number;
  carbohydrates: number;
  calories: number;
  price: number;
  image: string;
}

// Функция сортировки с типизацией
const sortProducts = (products: Product[]): Product[] => {
  const order = ["bun", "sauce", "main"];
  return products
    .slice()
    .sort((a, b) => order.indexOf(a.type) - order.indexOf(b.type));
};

const BurgerIngredients: React.FC = () => {
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    setIsModalOpen(true);
  };
  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedProduct(null);
  };

  // Проверка наличия продуктов и их типизации
  if (!Array.isArray(products)) {
    console.error("products is not an array");
    return null;
  }
  products.forEach((product) => {
    if (
      !product._id ||
      !product.name ||
      !product.type ||
      !product.price ||
      !product.image
    ) {
      console.error("Product is missing required fields", product);
    }
  });

  const sortedProducts = sortProducts(products as Product[]);
  const [current, setCurrent] = React.useState("bun");

  // Рефы для каждого раздела
  const bunRef = useRef<HTMLDivElement>(null);
  const sauceRef = useRef<HTMLDivElement>(null);
  const mainRef = useRef<HTMLDivElement>(null);

  // Функция для прокрутки к нужному разделу
  const handleTabClick = (value: string) => {
    setCurrent(value);
    switch (value) {
      case "bun":
        bunRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "sauce":
        sauceRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "main":
        mainRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      default:
        break;
    }
  };

  return (
    <>
      <h1>Список продуктов</h1>

      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={current === "bun"}
          onClick={() => handleTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={current === "sauce"}
          onClick={() => handleTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={current === "main"}
          onClick={() => handleTabClick("main")}
        >
          Начинки
        </Tab>
      </div>

      <div className={ingredient.ingredients}>
        <div ref={bunRef}>
          <h2 className={ingredient.ingredientTitle}>Булки</h2>
          <ul className={ingredient.ingredientsContainer}>
            {sortedProducts
              .filter((product) => product.type === "bun")
              .map((product) => (
                <li
                  className={ingredient.ingredientsItem}
                  key={product._id}
                  onClick={() => handleProductClick(product)}
                >
                  <img src={product.image} alt={product.name} />
                  <p className={ingredient.price}>Цена: {product.price} руб.</p>
                  <h3 className={ingredient.name}>{product.name}</h3>
                </li>
              ))}
          </ul>
        </div>
        <div ref={sauceRef}>
          <h2 className={ingredient.ingredientTitle}>Соусы</h2>
          <ul className={ingredient.ingredientsContainer}>
            {sortedProducts
              .filter((product) => product.type === "sauce")
              .map((product) => (
                <li
                  className={ingredient.ingredientsItem}
                  key={product._id}
                  onClick={() => handleProductClick(product)}
                >
                  <img src={product.image} alt={product.name} />
                  <p className={ingredient.price}>Цена: {product.price} руб.</p>
                  <h3 className={ingredient.name}>{product.name}</h3>
                </li>
              ))}
          </ul>
        </div>
        <div ref={mainRef}>
          <h2 className={ingredient.ingredientTitle}>Начинки</h2>
          <ul className={ingredient.ingredientsContainer}>
            {sortedProducts
              .filter((product) => product.type === "main")
              .map((product) => (
                <li
                  className={ingredient.ingredientsItem}
                  key={product._id}
                  onClick={() => handleProductClick(product)}
                >
                  <img src={product.image} alt={product.name} />
                  <p className={ingredient.price}>Цена: {product.price} руб.</p>
                  <h3 className={ingredient.name}>{product.name}</h3>
                </li>
              ))}
          </ul>
        </div>
      </div>
      <Modal
        show={isModalOpen}
        onClose={handleCloseModal}
        product={selectedProduct}
      />
    </>
  );
};

export default BurgerIngredients;
