// src/ui/ModalWindow/index.tsx

import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../../redux/store"; // Подставьте путь к вашему корневому редуктору и хранилищу
import { closeModal } from "./modalActions";
import styles from "./styles.module.css";

interface ModalWindowProps {
  children: React.ReactNode;
}

const ModalWindow: React.FC<ModalWindowProps> = ({ children }) => {
  const active = useSelector((state: RootState) => state.modal.active);
  const dispatch = useDispatch();

  useEffect(() => {
    const handleModalKeyClose = (event: KeyboardEvent) => {
      if (event.key === "Escape") {
        dispatch(closeModal());
      }
    };

    if (active) {
      window.addEventListener("keydown", handleModalKeyClose);
    }

    return () => {
      window.removeEventListener("keydown", handleModalKeyClose);
    };
  }, [active, dispatch]);

  return (
    <div
      className={active ? `${styles.modal} ${styles.active}` : styles.modal}
      onClick={() => dispatch(closeModal())}
    >
      <div
        className={styles.modal__content}
        onClick={(e) => e.stopPropagation()}
      >
        {children}
      </div>
    </div>
  );
};

export default ModalWindow;
