import React from "react";
import { useSelector, useDispatch } from "react-redux";
import BurgerConstructor from "./BurgerConstructor";
import BurgerIngredients from "./BurgerIngredients";
import styles from "./styles.module.css";
import { Product } from "./BurgerIngredients/types";
import {
  addIngredient,
  removeIngredient,
} from "./BurgerConstructor/services/actions/burgerActions";

const BurgerContent: React.FC = () => {
  const dispatch = useDispatch();
  const selectedIngredients = useSelector(
    (state: any) => state.burger.selectedIngredients
  );

  // Функция для добавления ингредиента в конструктор
  const handleAddIngredient = (ingredient: Product) => {
    dispatch(addIngredient(ingredient));
  };

  // Функция для удаления ингредиента из конструктора
  const handleRemoveIngredient = (index: number) => {
    dispatch(removeIngredient(index));
  };

  return (
    <div className={styles.burgerContent}>
      <div>
        <BurgerIngredients />
      </div>
      <div>
        <BurgerConstructor
          selectedIngredients={selectedIngredients}
          onAddIngredient={handleAddIngredient}
          onRemoveIngredient={handleRemoveIngredient}
        />
      </div>
    </div>
  );
};

export default BurgerContent;
