import React from "react";
import products from "../../utils/data";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";


// Типизация для продукта
interface Product {
  _id: string;
  name: string;
  type: string;
  proteins: number;
  fat: number;
  carbohydrates: number;
  calories: number;
  price: number;
  image: string;
}

// Функция сортировки с типизацией
const sortProductsByType = (products: Product[]): Product[] => {
  const order = ["bun", "sauce", "main"];
  return products
    .slice()
    .sort((a, b) => order.indexOf(a.type) - order.indexOf(b.type));
};

const ProductListTwo: React.FC = () => {
  const sortedProducts = sortProductsByType(products);
const [current, setCurrent] = React.useState("one");
  return (
    <>
      <h1>Список продуктов</h1>

      <div style={{ display: "flex" }}>
        <Tab value="one" active={current === "one"} onClick={setCurrent}>
          One
        </Tab>
        <Tab value="two" active={current === "two"} onClick={setCurrent}>
          Two
        </Tab>
        <Tab value="three" active={current === "three"} onClick={setCurrent}>
          Three
        </Tab>
      </div>
      <ul>
        {sortedProducts.map((product) => (
          <li key={product._id}>
            <h2>{product.name}</h2>
            <p>Тип: {product.type}</p>
            <p>Протеин: {product.proteins}</p>
            <p>Жиры: {product.fat}</p>
            <p>Углеводы: {product.carbohydrates}</p>
            <p>Калории: {product.calories}</p>
            <p>Цена: {product.price} руб.</p>
            <img src={product.image} alt={product.name} />
          </li>
        ))}
      </ul>
    </>
  );
};

export default ProductListTwo;
