import React, { useRef, useState, useEffect } from "react";
import { useDrag, useDrop } from "react-dnd";
import {
  ConstructorElement,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { Product } from "../BurgerIngredients/types";
import styles from "./burgerStyles.module.css";

interface DraggableIngredientProps {
  ingredient: Product & { uniqueId: string };
  index: number;
  moveIngredient: (dragIndex: number, hoverIndex: number) => void;
  onRemoveIngredient: (uniqueId: string) => void;
}

const DraggableIngredient: React.FC<DraggableIngredientProps> = ({
  ingredient,
  index,
  moveIngredient,
  onRemoveIngredient,
}) => {
  const ref = useRef<HTMLDivElement>(null);
  const [dragging, setDragging] = useState(false);
  const [hoverIndex, setHoverIndex] = useState<number | null>(null);

  const [, drop] = useDrop({
    accept: "INGREDIENT",
    hover(item: { index: number }) {
      const dragIndex = item.index;
      const hoverIndex = index;

      if (!ref.current) return;
      if (dragIndex === hoverIndex) return;

      moveIngredient(dragIndex, hoverIndex);
      item.index = hoverIndex;
    },
  });

  const [{ isDragging }, drag, preview] = useDrag({
    type: "INGREDIENT",
    item: { index },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
    end: () => {
      setDragging(false);
    },
  });

  useEffect(() => {
    if (dragging) {
      preview(drop(ref));
    } else {
      drag(drop(ref));
    }
  }, [dragging, drag, drop, preview]);

  const handleMouseDown = (e: React.MouseEvent<HTMLDivElement>) => {
    setDragging(true);
    setHoverIndex(index);
  };

  const handleMouseUp = () => {
    setDragging(false);
    setHoverIndex(null);
  };

  return (
    <div
      ref={ref}
      className={styles.constructorElement}
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: dragging ? "grabbing" : "grab",
      }}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
    >
      <span className={styles.dragHandle}>
        <DragIcon type="primary" />
      </span>
      <ConstructorElement
        text={ingredient.name}
        price={ingredient.price}
        thumbnail={ingredient.image}
        handleClose={() => onRemoveIngredient(ingredient.uniqueId)}
      />
    </div>
  );
};

export default DraggableIngredient;
