import React, { useRef, useState } from "react";
import products from "../../../utils/data";
import ingridient from "./ingridient.module.css";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import Modal from "../../../utils/modal-test/modal";
import ModalWindow from "../../../utils/ModalWindow/ModalWindow";

// Типизация для продукта
interface Product {
  _id: string;
  name: string;
  type: string;
  proteins: number;
  fat: number;
  carbohydrates: number;
  calories: number;
  price: number;
  image: string;
}

// Функция сортировки с типизацией
const sortProducts = (products: Product[]): Product[] => {
  const order = ["bun", "sauce", "main"];
  return products
    .slice()
    .sort((a, b) => order.indexOf(a.type) - order.indexOf(b.type));
};

const BurgerIngridients: React.FC = () => {
  const sortedProducts = sortProducts(products as Product[]);
  const [current, setCurrent] = React.useState("bun");

  // Рефы для каждого раздела
  const bunRef = useRef<HTMLDivElement>(null);
  const sauceRef = useRef<HTMLDivElement>(null);
  const mainRef = useRef<HTMLDivElement>(null);

  // Состояние для модального окна
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
const [isModalActive, setIsModalActive] = useState(false);

  // Функция для прокрутки к нужному разделу
  const handleTabClick = (value: string) => {
    setCurrent(value);
    switch (value) {
      case "bun":
        bunRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "sauce":
        sauceRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      case "main":
        mainRef.current?.scrollIntoView({ behavior: "smooth" });
        break;
      default:
        break;
    }
  };

  // Функция для открытия модального окна
  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
  };


  return (
    <>
      <h1>Список продуктов</h1>

      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={current === "bun"}
          onClick={() => handleTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={current === "sauce"}
          onClick={() => handleTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={current === "main"}
          onClick={() => handleTabClick("main")}
        >
          Начинки
        </Tab>
      </div>

      <div className={ingridient.ingridients}>
        <div ref={bunRef}>
          <h2 className={ingridient.ingridientTitle}>Булки</h2>
          <ul className={ingridient.ingridientsContainer}>
            {sortedProducts
              .filter((product) => product.type === "bun")
              .map((product) => (
                <li
                  className={ingridient.ingridientsItem}
                  key={product._id}
                  onClick={() => handleProductClick(product)}
                >
                  <img src={product.image} alt={product.name} />
                  <p className={ingridient.price}>Цена: {product.price} руб.</p>
                  <h3 className={ingridient.name}>{product.name}</h3>
                </li>
              ))}
          </ul>
        </div>
        <div ref={sauceRef}>
          <h2 className={ingridient.ingridientTitle}>Соусы</h2>
          <ul className={ingridient.ingridientsContainer}>
            {sortedProducts
              .filter((product) => product.type === "sauce")
              .map((product) => (
                <li
                  className={ingridient.ingridientsItem}
                  key={product._id}
                  onClick={() => handleProductClick(product)}
                >
                  <img src={product.image} alt={product.name} />
                  <p className={ingridient.price}>Цена: {product.price} руб.</p>
                  <h3 className={ingridient.name}>{product.name}</h3>
                </li>
              ))}
          </ul>
        </div>
        <div ref={mainRef}>
          <h2 className={ingridient.ingridientTitle}>Начинки</h2>
          <ul className={ingridient.ingridientsContainer}>
            {sortedProducts
              .filter((product) => product.type === "main")
              .map((product) => (
                <li
                  className={ingridient.ingridientsItem}
                  key={product._id}
                  onClick={() => handleProductClick(product)}
                >
                  <img src={product.image} alt={product.name} />
                  <p className={ingridient.price}>Цена: {product.price} руб.</p>
                  <h3 className={ingridient.name}>{product.name}</h3>
                </li>
              ))}
          </ul>
        </div>
      </div>

      {selectedProduct && (
        <ModalWindow active={isModalActive} setActive={setIsModalActive}>
          <img src={selectedProduct.image} alt={selectedProduct.name} />
          <p>Протеинов: {selectedProduct.proteins}</p>
          <p>Жиров: {selectedProduct.fat}</p>
          <p>Углеводов: {selectedProduct.carbohydrates}</p>
          <p>Калорий: {selectedProduct.calories}</p>
          <p>Цена: {selectedProduct.price} руб.</p>
        </ModalWindow>
      )}
    </>
  );
};

export default BurgerIngridients;
