import React from "react";
import products, { Product } from "../../utils/data";
import {
  Tab,
  Button,
} from "@ya.praktikum/react-developer-burger-ui-components";

const MyAppTest: React.FC = () => {
  const [current, sethandleTabClick] = React.useState("bun");

  const setCurrent = (value: string) => {
    sethandleTabClick(value); 
  };

  const filteredProducts = products.filter(
    (product: Product) => product.type === current
  );

  return (
    <div>
      <h1>Список продуктов</h1>

      <div style={{ display: "flex", marginBottom: "20px" }}>
        <Tab
          value="bun"
          active={current === "bun"}
          onClick={() => setCurrent("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={current === "sauce"}
          onClick={() => setCurrent("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={current === "main"}
          onClick={() => setCurrent("main")}
        >
          Начинки
        </Tab>
      </div>

      <ul style={{ listStyleType: "none", padding: 0 }}>
        {filteredProducts.map((data: Product) => (
          <li
            key={data._id}
            style={{
              marginBottom: "20px",
              borderBottom: "1px solid #ccc",
              paddingBottom: "20px",
            }}
          >
            <h2>{data.name}</h2>
          
            <p>Протеин: {data.proteins}</p>
            <p>Жиры: {data.fat}</p>
            <p>Углеводы: {data.carbohydrates}</p>
            <p>Калории: {data.calories}</p>
            <p>Цена: {data.price} руб.</p>
            <img
              src={data.image}
              alt={data.name}
              style={{ width: "150px", height: "auto" }}
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MyAppTest;
